import React from 'react';
import './css/InputField.css'

class InputField extends React.Component
{
	constructor(props) {
    super(props);
    this.state = { text : ''};
  	this.handleInput = this.handleInput.bind(this);
  	this.handlePress = this.handlePress.bind(this);
  	}


  	handleInput(event){
  		  this.setState({text : event.target.text})
  	}

  	handlePress(event){
  		if (event.key == "Enter"){
  		this.setState({text : "" });
  	    }
  	}

  	componentDidMount() {
      this.chatID = setInterval(() => this.update(), 0.001);
    }

    componentWillUnmount() {
      clearInterval(this.chatID);
    }

    render(){
    	const tt=this.state.text;
    	return(
    		<div className="InputField"> 
    			<textarea value={this.state.text} onChange={this.handleInput}  onKeyPress={this.handlePress}/>
    			<div className="text"> {tt} </div>
    		</div>///
    	);
    }
}

export default InputField;
